import { ReactNode } from 'react';
import {
    Box,
    Flex,
    Avatar,
    HStack,
    Link,
    IconButton,
    Button,
    Menu,
    MenuButton,
    MenuList,
    MenuItem,
    MenuDivider,
    useDisclosure,
    useColorModeValue,
    Stack,
} from '@chakra-ui/react';
import { HamburgerIcon, CloseIcon, AddIcon } from '@chakra-ui/icons';

const Links = {
    0: {
        title: "Курсы",
        id: 0,
        link: "/courses"
    },
    1: {
        title: "О платформе",
        id: 1,
        link: "/about"
    },
    2: {
        title: "Пожертвовать",
        id: 2,
        link: "/donation"
    },
};

const NavLink = ({ children , link}) => (
    <Link
        px={2}
        py={1}
        rounded={'md'}
        _hover={{
            textDecoration: 'none',
            bg: useColorModeValue('gray.200', 'gray.700'),
        }}
        href={link}>
        {children}
    </Link>
);

export const  Navbar = () => {
    const { isOpen, onOpen, onClose } = useDisclosure();

    return (
        <>
            <Box bg={useColorModeValue('gray.100', 'gray.900')} px={4}>
                <Flex h={16} alignItems={'center'} justifyContent={'space-between'}>
                    <IconButton
                        size={'md'}
                        icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
                        aria-label={'Open Menu'}
                        display={{ md: 'none' }}
                        onClick={isOpen ? onClose : onOpen}
                    />
                    <HStack spacing={8} alignItems={'center'}>
                        <Box>
                            <svg width="43" height="31" viewBox="0 0 43 31" fill="none" xmlns="http://www.w3.org/2000/svg" >
                                <rect width="43" height="31" fill="url(#pattern0)"/>
                                <defs>
                                    <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                                        <use href="#image0" transform="translate(-1.11161 -1.41474) scale(0.00446429 0.00609908)"/>
                                    </pattern>
                                    <image id="image0" width="720" height="600" href="data:image/png;base64,"/>
                                </defs>
                            </svg>
                        </Box>
                        <HStack
                            as={'nav'}
                            spacing={4}
                            display={{ base: 'none', md: 'flex' }}>
                            {Object.keys({...Links}).map(key => {
                                return(
                                    <NavLink key={Links[key].id}>{Links[key].title}</NavLink>
                                )
                            })}
                        </HStack>
                    </HStack>
                    <Flex alignItems={'center'}>

                        <Menu>
                            <MenuButton
                                as={Button}
                                rounded={'full'}
                                variant={'link'}
                                cursor={'pointer'}
                                minW={0}>
                                Процессы

                                <HamburgerIcon />
                            </MenuButton>
                            <MenuList>
                                <MenuItem>Link 1</MenuItem>
                                <MenuItem>Link 2</MenuItem>
                                <MenuDivider />
                                <MenuItem>Link 3</MenuItem>
                            </MenuList>
                        </Menu>
                    </Flex>
                </Flex>

                {isOpen ? (
                    <Box pb={4} display={{ md: 'none' }}>
                        <Stack as={'nav'} spacing={4}>
                            {Object.keys({...Links}).map(key => {
                                return(
                                    <NavLink key={Links[key].id} link={Links[key].link}>{Links[key].title}</NavLink>
                                )
                            })}
                        </Stack>
                    </Box>
                ) : null}
            </Box>


        </>
    );
}